import { useState, useTransition } from 'react';
import './App.css';
import Header from './Components/Header';
import DnsSearcher from './Components/Dnssearcher';
function App() {

  //permite actualizar el estado de los componentes sin bloquear la UI
  const [estaPendiente, cambiarMenu] = useTransition();
  //guardo que menu ha seleccionado
  const [menu, seleccionarMenu] = useState("");
  const [historial,editarHistorial] =useState([])
  const editarLista = (busqueda) =>{
    editarHistorial([...historial,busqueda])
  }

  return (
  <div className='App'>
    <Header SelectorMenu={seleccionarMenu} /> 
    <div>
      {menu ==="home" && <p>hola home</p>}
      {menu ==="DNS" && <DnsSearcher/>}
      {menu ==="Pass" && <p>hola Pass</p>}
    </div> 

  </div>
  );
}

export default App;
