import React, { useState } from "react"
import { Button } from "react-bootstrap";
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';

const DnsSearcher = ({editarLista}) => {


const  [busqueda , agregarabusqueda] = useState( {txtbusqueda:"" , ip:""})

const  [estado,setEstado] =useState("vacio");

const {txtbusqueda,ip} = busqueda;

const handleChange = (e) => { 
  console.log("tomos los datos")
  agregarabusqueda({
    ...busqueda,
      [e.target.name] : e.target.value
  })
}


const submitForm=(e)=>{
  e.preventDefault();
  if (! validarUrl(txtbusqueda)){
    setEstado("error");
    return;
  }

  var ipweb = obtenerDireccionIP(txtbusqueda)
  agregarabusqueda({txtbusqueda:txtbusqueda ,ip:ipweb})


  editarLista(busqueda);
  console.log(busqueda);

  agregarabusqueda({txtbusqueda:"",ip:""});

  setEstado("validado");
  return;
}

function validarUrl(txtbusqueda){
  if (txtbusqueda.trim()==="") { return false;}

  return true;
}



async function obtenerDireccionIP(url) {
  try {
    const response = await fetch(`http://ip-api.com/json/${url}`);
    const data = await response.json();

    if (data.status === 'success') {
      const ip = data.query;
      return ip;
    } else {
      console.error('Error al obtener la direcci贸n IP:', data.message);
      return null;
    }
  } catch (error) {
    console.error('Error al obtener la direcci贸n IP:', error);
    return null;
  }
}


  const DnsLookup =  async() => {
    try{
      const api = await fetch("https://thecocktaildb.com/api/json/v1/1/random.php");
      console.log(api);
      const resultado2 = await api.json();
      console.log(resultado2);

    }
    catch(e){
      console.log(e)
    }
  }

  return(
        <>
      <Form onSubmit={submitForm}>
      <div className="row">
        <div className="col-md-8 col-sm-12">
        <InputGroup className="mb-3">
        <InputGroup.Text id="basic-addon1">Web a buscar:</InputGroup.Text>
          <Form.Control
            type="text"
            placeholder="ingrese una url para escanear"
            name="txtbusqueda"
            onChange={handleChange}
            value={txtbusqueda}/>
        </InputGroup>
      </div>
        <div className="col-md-4 col-sm-12"  >
          <Button type="submit" variant="primary">Buscar</Button>
        </div>
      </div>
      </Form>
      {estado ==="error"? 
               <h2> la pagina ingresada es invalida</h2>
        : estado==="validado"? 
              <h2>pagina {ip}</h2>
              :null
        }
      </>
    )
}

export default DnsSearcher;
